<?php

ini_set("display_errors", 1);
error_reporting(E_ALL);

set_time_limit(300);
ini_set('max_execution_time', 500);

defined('BASE_PATH') || define('BASE_PATH', realpath(dirname(__FILE__)));

$base_url = "http://{$_SERVER["HTTP_HOST"]}";
$url = "https://updates02.siberiancms.com/installs/get.php";
$error_message = "";

$archive_base_path = BASE_PATH."/siberiancms.source.tgz";

if (array_key_exists("sae", $_POST) &&
    $_POST['sae'] === "1") {
    try {

        //adding license to get url
        $url .= "?sae=1";

        if(version_compare(phpversion(), "5.6", "<")) {
            throw new Exception("PHP 5.6+ is required, see <a href=\"https://doc.siberiancms.com/knowledge-base/siberian-server-requirements/\">siberian-server-requirements</a> for more informations \n");
        }

        Requirements::runTest();

        if(!fileIsValid()) {
            downloadFile();
        }

        if(fileIsValid()) {
            extractFile();

            if(!checkContents()) {
                throw new Exception("Unable to extract the package. Please, check the exec function is not disabled and your SiberianCMS installation folder permissions.");
            }

            setPermissions();

            @unlink($archive_base_path);

            sleep(5);
            header("Location: {$base_url}");
            die;

        } else {
            throw new Exception("The content of the package seems to be corrupted. Please, try again.");
        }

    } catch(Exception $e) {
        $error_message = $e->getMessage();
    }
}

function checkPermissions() {
    return is_writable(BASE_PATH);
}

function checkFunctions() {
    return function_exists("exec");
}

function fileIsValid($md5 = null) {
    global $archive_base_path;
    return is_file($archive_base_path) && @md5_file($archive_base_path) == _getMd5();
}

function downloadFile() {
    global $archive_base_path;
    if(is_file($archive_base_path)) @unlink($archive_base_path);
    $resFileOpen = @fopen(_getFileUrl(), "r");
    if($resFileOpen) {
        file_put_contents($archive_base_path, $resFileOpen);
    } else {
        throw new Exception("Cannot download install file.");
    }
}

function extractFile() {
    global $archive_base_path;
    exec('tar -xzf "'.$archive_base_path.'"');
}

function checkContents() {
    $base_file_path = BASE_PATH."/check_connection.php";
    return is_file($base_file_path) && @file_get_contents($base_file_path) == "1";
}

function setPermissions() {

    $collection = new RecursiveIteratorIterator(new RecursiveDirectoryIterator(BASE_PATH));
    $full_permissions_directories = array("images", "languages", "var");
    foreach($collection as $item) {

        if(in_array($item->getFilename(), array(".", "..", ".DS_Store", ".htaccess"))) {
            continue;
        }

        $path = str_replace(BASE_PATH, "", $item->getRealPath());
        if(substr($path, 0, 1) == "/") {
            $path = substr($path, 1, strlen($path) -1);
        }
        if(empty($path)) {
            continue;
        }

        $part = current(explode("/", $path));

        if(empty($part)) {
            continue;

        }
        if($item->isDir() && in_array($part, $full_permissions_directories)) {
            @chmod($item->getRealPath(), 0777);
        } else if($item->isDir()) {
            @chmod($item->getRealPath(), 0775);
        } else if($item->isFile() && in_array($part, $full_permissions_directories)) {
            @chmod($item->getRealPath(), 0666);
        } else if($item->isFile() && $item->getRealpath() == BASE_PATH."/index.php") {
            @chmod($item->getRealPath(), 0644);
        } else if($item->isFile()) {
            @chmod($item->getRealPath(), 0664);
        }

    }
}

function _getMd5() {
    global $md5;
    if(!$md5) {
        _fetchData();
    }
    return $md5;
}

function _getFileUrl() {
    global $file_url;
    if(!$file_url) {
        _fetchData();
    }
    return $file_url;
}

function _fetchData() {

    global $url, $md5, $file_url;

    $data = @file_get_contents($url);

    $dataDecoded = @json_decode($data);

    if($dataDecoded &&
        isset($dataDecoded->error) &&
        isset($dataDecoded->message)) {
        if($dataDecoded->error === 1) {
            throw new Exception($dataDecoded->message);
        }
    }

    if(empty($dataDecoded) ||
        empty($dataDecoded->url) ||
        empty($dataDecoded->md5)) {
        throw new Exception("Unable to communicate with SiberianCMS server.");
    }

    $file_url = $dataDecoded->url."&licensekey=";
    $md5 = $dataDecoded->md5;
}

class Requirements {

    public static $_functions = array(
        "exec",
    );

    public static $_extensions = array(
        "SimpleXML",
        "pdo_mysql",
        "gd",
        "mbstring",
        "iconv",
        "curl",
        "openssl",
    );

    public static $_binaries = array(
        "zip",
        "unzip",
    );

    public static $_errors = array();

    public static function runTest() {

        self::testFunctions();
        self::testExtensions();
        self::testExec();

        if(!empty($_errors)) {
            throw new Exception(implode("\n",array(
                "Following requirements are missing: \n",
                implode("\n", $_errors),
                "...\n"
            )));
        }
    }

    /**
     *
     */
    public static function testFunctions() {
        foreach(self::$_functions as $function) {
            if(!function_exists($function)) {
                $_errors[] = "Please enable/add function: {$function}()";
            }
        }
    }

    /**
     *
     */
    public static function testExtensions() {
        foreach(self::$_extensions as $extension) {
            if(!extension_loaded($extension)) {
                $_errors[] = "Please enable/add extension: {$extension}";
            }
        }
    }

    /**
     *
     */
    public static function testExec() {
        if(function_exists("exec")) {
            $which1 = exec("which zip");
            if(empty($which1)) {
                $_errors[] = "Please enable/add binary: zip";
            }

            $which2 = exec("which unzip");
            if(empty($which2)) {
                $_errors[] = "Please enable/add binary: unzip";
            }

        } else {
            $_errors[] = "Please enable/add function: exec()";
        }

        if(OPENSSL_VERSION_NUMBER < 268439647) {
            $_errors[] = "Please update OpenSSL to 1.0.1+";
        }
    }

}

